pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

library strings {
    function compareStrings(string memory a, string memory b)
        public
        pure
        returns (bool)
    {
        return (keccak256(abi.encodePacked((a))) ==
            keccak256(abi.encodePacked((b))));
    }

    function bytes32ToString(bytes32 _bytes32)
        public
        pure
        returns (string memory)
    {
        uint8 i = 0;
        while (i < 32 && _bytes32[i] != 0) {
            i++;
        }
        bytes memory bytesArray = new bytes(i);
        for (i = 0; i < 32 && _bytes32[i] != 0; i++) {
            bytesArray[i] = _bytes32[i];
        }
        return string(bytesArray);
    }

    function stringToBytes32(string memory source)
        public
        pure
        returns (bytes32 result)
    {
        bytes memory tempEmptyStringTest = bytes(source);
        if (tempEmptyStringTest.length == 0) {
            return 0x0;
        }

        assembly {
            // solhint-disable-line no-inline-assembly
            result := mload(add(source, 32))
        }
    }
}

contract EscrowFactory is Ownable {
    address linkToken;
    address oracle;
    string jobId;
    uint256 public maxLockPeriod;
    uint256 public productCount;
    mapping(uint256 => address) public products;

    constructor(
        address _linkToken,
        address _oracle,
        string memory _jobId,
        uint256 _maxLockPeriod
    ) {
        require(_maxLockPeriod >= 0, "lock period must be greater than 0");
        linkToken = _linkToken;
        oracle = _oracle;
        jobId = _jobId;
        productCount = 0;
        maxLockPeriod = _maxLockPeriod;
    }

    event ProductCreated(address indexed seller, address product);

    function createProduct(
        string memory _name,
        uint256 _price,
        uint256 _lockPeriod
    ) external {
        uint256 lockPeriod = _lockPeriod;
        if (lockPeriod > maxLockPeriod) {
            lockPeriod = maxLockPeriod;
        }
        bytes32 salt = keccak256(
            abi.encodePacked(
                linkToken,
                oracle,
                jobId,
                _name,
                msg.sender,
                _price,
                lockPeriod,
                productCount
            )
        );

        address product = address(new Escrow{salt: salt}());

        Escrow(product).init(
            linkToken,
            oracle,
            jobId,
            _name,
            msg.sender,
            _price,
            lockPeriod
        );

        productCount++;
        products[productCount] = product;

        emit ProductCreated(msg.sender, product);
    }

    function setMaxLockPeriod(uint256 _maxLockPeriod) external onlyOwner {
        require(_maxLockPeriod >= 0, "lock period must be greater than 0");
        maxLockPeriod = _maxLockPeriod;
    }
}

contract Escrow is ChainlinkClient, Ownable {
    using Chainlink for Chainlink.Request;
    using strings for string;
    using strings for bytes32;

    uint256 private constant ORACLE_PAYMENT = 1 * LINK_DIVISIBILITY;
    address private oracle;
    bytes32 private jobId;

    address factory;
    uint256 public lockPeriod;
    uint256 public currentBlock;

    struct Product {
        uint256 id;
        string name;
        uint256 price;
        address owner;
        address buyer;
        // IERC20 currency;
        bool purchased;
        string trackingNo;
        string deliveryStatus;
        Stage stage;
    }

    Product public product;

    // address public buyer;
    // address public seller;
    // uint256 public price;

    enum Stage {
        Initiate,
        WaitForShipping,
        Shipping,
        Delivered,
        End
    }

    event OrderInitiate(address indexed _buyer);
    event OrderCancel(address indexed _buyer);

    event ShipmentInprogress(string trackingNo);
    event ShipmentUpdated(bytes32 status);
    event ShipmentDelivered(bytes32 status);
    event OrderCompleted(string trackingNo);

    modifier validStage(Stage _stage, string memory message) {
        require(product.stage == _stage, message);
        _;
    }

    modifier onlyBuyer() {
        require(msg.sender == product.buyer, "Only Buyer");
        _;
    }

    constructor() {
        factory = msg.sender;
    }

    function init(
        address _link,
        address _oracle,
        string memory _jobId,
        string memory _name,
        address _seller,
        // address _currency,
        uint256 _price,
        uint256 _lockPeriod
    ) external {
        require(msg.sender == factory, "FORBIDDEN");
        // setPublicChainlinkToken();
        setChainlinkToken(_link);
        transferOwnership(_seller);
        oracle = _oracle;
        jobId = _jobId.stringToBytes32();
        lockPeriod = _lockPeriod;

        product.name = _name;
        product.stage = Stage.Initiate;
        product.owner = _seller;
        // currency = IERC20(_currency);
        product.price = _price;
    }

    function order()
        external
        payable
        validStage(Stage.Initiate, "Already have a buyer")
    {
        require(msg.sender != owner(), "You can not buy from yourself");
        require(msg.value >= product.price, "Not enough fund");
        product.stage = Stage.WaitForShipping;
        product.buyer = msg.sender;
        currentBlock = block.number;
        // currency.transferFrom(msg.sender, address(this), price);
        emit OrderInitiate(product.buyer);
    }

    function cancelOrder()
        external
        onlyBuyer
        validStage(Stage.WaitForShipping, "shipping in progress")
    {
        require(
            block.number >= currentBlock + lockPeriod,
            "Not allowed to cancel order"
        );
        product.buyer = address(0);
        product.stage = Stage.Initiate;
        // currency.transfer(msg.sender, price);
        payable(msg.sender).transfer(address(this).balance);
        emit OrderCancel(msg.sender);
    }

    function updateShipment(string memory _trackingNO)
        external
        onlyOwner
        validStage(Stage.WaitForShipping, "Invalid Stage")
    {
        product.stage = Stage.Shipping;
        product.trackingNo = _trackingNO;
        emit ShipmentInprogress(product.trackingNo);
    }

    function requestShippingDetail()
        external
        validStage(Stage.Shipping, "Need shipment")
    {
        Chainlink.Request memory req = buildChainlinkRequest(
            jobId,
            address(this),
            this.fulfillShippingDetail.selector
        );

        req.add("trackingNo", product.trackingNo);
        bytes32 requestId = sendChainlinkRequestTo(oracle, req, ORACLE_PAYMENT);
        emit ChainlinkRequested(requestId);
    }

    function fulfillShippingDetail(bytes32 _requestId, bytes32 _deliveryStatus)
        public
        recordChainlinkFulfillment(_requestId)
    {
        product.deliveryStatus = _deliveryStatus.bytes32ToString();
        if (product.deliveryStatus.compareStrings("Delivered")) {
            product.stage = Stage.Delivered;
            emit ShipmentDelivered(_deliveryStatus);
        } else {
            emit ShipmentUpdated(_deliveryStatus);
        }
    }

    function reclaimFund()
        external
        onlyOwner
        validStage(Stage.Delivered, "Invalid Stage")
    {
        // currency.transfer(msg.sender, address(this).balance);
        product.stage = Stage.End;
        payable(msg.sender).transfer(address(this).balance);
    }
}
